import React, {Component} from 'react';
import {Redirect} from 'react-router-dom';
import {ajaxPost} from '../../Services/Http/Ajax';
import {STATUS_ACTIVE} from '../../Utils/Constants';
import {API_URL} from '../../Utils/Urls';
import {ControlLabel} from "react-bootstrap";

const HousingTypeFormComponent = (props) => {
    const [homeLessSelect, setHomeLessSelect] = React.useState(false);
    const [housingType, setHousingType] = React.useState([]);
    const [housingTypeDataDropDown, setHousingTypeData] = React.useState();
    let checkbox_form,housing_type_form='';
    let t='';

    React.useEffect(() => {
        fetchHousingType(homeLessSelect);
    }, []);

    const handleChange = (e) => {
        let childFamilyData = {
            housingData: {
                housing_type: e.target.value
            }
        };
        props.onSelectedChild(childFamilyData);
    };

    const fetchHousingType = (isChecked) => {
        let uri = API_URL.GET_HOUSINGTYPE;
        let data = {
            housing_type: isChecked
        };
        ajaxPost(uri, data, (response) => {
            if (response.status === STATUS_ACTIVE) {
                setHousingType(response.content);
            }

        });
    };


    const handleHomelessSelect = (e) => {
        setHomeLessSelect(e.target.checked);
        fetchHousingType(e.target.checked);
    };

    let formDataBuildOne = props.famData ? ((housing_type_form=props.famData.housing_type ))? props.famData : '' :'';


    React.useEffect((props) => {
        if (housing_type_form) {
            let uri = API_URL.GET_HOUSINGTYPE_DATA;
            let data = {
                housing_type_id: housing_type_form
            };
            ajaxPost(uri, data, (response) => {
                if (response.status === STATUS_ACTIVE) {
                    setHousingTypeData(response.content);
                    t=response.content;
                }
            });
        }
    }, [housing_type_form]);

    return (
        <div className="label-container">
            <label>Homeless</label>

            <input  className="default-margin-homeless"   type="checkbox" checked = {homeLessSelect} name="housing_type_checkbox"  onChange={handleHomelessSelect} />
            <label className="default-margin-housing-type">Housing type</label>

            <select id="housing_type" name="housing_type" onChange={handleChange} >
                {housing_type_form && (
                    <option selected="selected">
                        { housingTypeDataDropDown?housingTypeDataDropDown:""}
                    </option>)}
                <option value="">Select option</option>
                {
                    housingType.map((value, index) => {
                            return <option key={index} value={value.id}>{value.housing_type}</option>
                        }
                    )
                }}
            </select>
        </div>
    )

};

export default HousingTypeFormComponent;
