//routing files
import React, {lazy, Component, Suspense} from 'react';
import Utils from '../Utils/Util';
import '../Assets/Styles/Common/style.css';
import * as constants from '../Utils/Constants';
import { BrowserRouter as Router, Switch, Route, Redirect} from 'react-router-dom';


const DashboardComponent = lazy(() => import("../Modules/Dashboard/DashboardComponent"));
const MenuPermissionContainer = lazy(() => import("../Modules/MenuPermission/MenuPermissionContainer"));
const App = lazy(() => import("../App"));

class Routes extends Component  {
    render() {
        let isUserLoggedOut = Utils.getItemLocalStorage('token') ? constants.STATUS_INACTIVE : constants.STATUS_ACTIVE;
        return (
            <Router>
                <Suspense fallback={<div className="loader"> </div>}>
                    <Switch>
                        <Route exact path="/" component={App}/>
                        <Route path="/dashboard" component={DashboardComponent}/>
                        <Route path="/menupermission" component={MenuPermissionContainer}/>
                    </Switch>
                    {isUserLoggedOut && (<Redirect to={'/'}/>)}
                </Suspense>
            </Router>
        );
    }
}

export default Routes;
