import React, {Component} from 'react';
import {Redirect} from 'react-router-dom';
import Utils from '../../Utils/Util';
import {ajaxPost} from '../../Services/Http/Ajax';
import * as constants from '../../Utils/Constants'


class DobFormComponent extends Component {
    constructor(props) {
        super(props);

        this.sudohandleChange = this.handleChange.bind(this);
        this.state = {
            value: "",
            dob: "",
            age: "",
            gender: "",
        };
    }

    handleChange = (e) => {
        const {name, value} = e.target;
        this.setState({[name] : value});

        let  childFamilyData= { dobData :{
            dob: this.state.dob,
            age: this.state.age,
            gender:  e.target.value}
        };
        this.props.onSelectedChild(childFamilyData);
    };




    agefun=()=>{
        let dobString=this.state.dob;
        console.log(dobString);
        let today = new Date();
        let birthDate = new Date(dobString);
        let age = today.getFullYear() - birthDate.getFullYear();
        let m = today.getMonth() - birthDate.getMonth();
        if (m < 0 || (m === 0 && today.getDate() < birthDate.getDate())) {
            age--;
        }
        return this.setState({
            age: age
        });
    };

    render() {
        return (
            <div>
                <p>
                    <label>DOB</label>
                    <input onChange={this.handleChange} onBlur={this.agefun} name="dob" id="dob" type="date" min='1899-01-01' />
                </p>
                <p>
                    <label>Age</label>
                    <input onChange={this.handleChange} value={this.state.age} name="age" id="age" type="number" />
                </p>
                <p>Please select your gender:</p>
                <input type="radio" name="gender" value="male" onChange={this.handleChange}/> <span> Male </span>
                <input type="radio" name="gender" value="female" onChange={this.handleChange} /> <span> Female </span>
                <input type="radio" name="gender" value="other" onChange={this.handleChange} /><span>Other</span>
            </div>
        )
    }
}
export default DobFormComponent;
