/**
 * Created by basil on 20/2/20.
 */

import React from 'react';
import '../../App.css';
import {showMessage} from '../../Utils/Util';
import {STATUS_ACTIVE} from '../../Utils/Constants';
import {API_URL} from '../../Utils/Urls';
import {ajaxPost,ajaxPut} from '../../Services/Http/Ajax';
import Checkbox from "../General/CheckBoxComponent";
import {ToastContainer} from "react-toastify";

const ModulePermissionComponent = (props) => {
    const [moduleFetched, setModuleFetched] = React.useState(false);
    const [modulePermissionArray, setModulePermissionArray] = React.useState([]);
    const [isMainModuleActive, setIsMainModuleActive] = React.useState(false);
    const {moduleId, roleId,agencyId } = props;
    const usePrevious = (value) => {
        const ref = React.useRef();
        React.useEffect(() => {
            ref.current = value;
        });
        return ref.current;
    }; console.log('add');
    const previousModuleId = usePrevious(moduleId);
    const previousRoleId = usePrevious(roleId);
    const previousAgencyId = usePrevious(agencyId);
    const getModulePermissionData = () => {
        if (roleId && moduleId) {
            let postData = {
                data:{
                    roleId: roleId,
                    moduleId: moduleId,
                    agencyId:agencyId
                }
            };
            let uri = API_URL.GET_MODULE_PERMISSION_URL;
          ajaxPost(uri, postData, getModulePermissionDataSuccess)
        }
    };
    const getModulePermissionDataSuccess = (response) => {
        if (response.status === STATUS_ACTIVE) {
            let moduleData;
            moduleData = response.content;
            let moduleNewTemp = moduleData.map((item)=> { return {...item }  } );
            setModulePermissionArray(moduleNewTemp);
            setModuleFetched(true);
            setIsMainModuleActive(moduleNewTemp[0].permission_is_active);
            console.log(moduleNewTemp[0].module_name);
            setIsMainModuleActive(moduleNewTemp[0].permission_is_active);
        }
    };
    const moduleCheckBoxDisabled = (e) => {
        setIsMainModuleActive(e.target.checked);
    };
    const handleChange = (e) => {
        /*const item = parseInt(e.target.id);
        setSubMenuArray(subMenuArray.map((elem)=>{
            let tempData = elem;
            if(tempData.sub_menu_id === item){
                tempData.is_active = e.target.checked;
            }
            return tempData;
        }));
        putMenuPermissionData(e)*/;
    };
    React.useEffect(() => {

        if(previousModuleId !== moduleId || previousRoleId !== roleId || previousAgencyId !== agencyId) {
            getModulePermissionData();
        }
    }, [moduleId, roleId,agencyId]);
    return (
        <div>
            <div className="data-bind-role">
                {
                    moduleFetched && (
                        <div className="data-bind-role">
                            <span className="btn text-info">{console.log(modulePermissionArray,'module')} {modulePermissionArray[0].module_name} <input onChange={moduleCheckBoxDisabled} checked={isMainModuleActive} type="checkBox" name="menuPermission"/></span>
                            <div>
                                { modulePermissionArray.map( (value, index) => {
                                    return <div key={index}>
                                        <div className="sub-menu-container">
                                            <div>{value.permission_name} </div>
                                            <div> <Checkbox name={`permission-${value.permission_id}`} dataid={value.permission_id} id={value.permission_id} checked={value.permission_is_active} disabled = {!isMainModuleActive} onChangeFunction={handleChange} /></div>
                                        </div>
                                    </div>
                                })
                                }
                            </div>
                        </div>
                    )}
            </div>
        </div>
    )
};

export default ModulePermissionComponent;
