import React, {Component} from 'react';
import {Redirect} from 'react-router-dom';
import {ajaxGet} from '../../Services/Http/Ajax';
import {STATUS_ACTIVE} from '../../Utils/Constants';
import {API_URL} from '../../Utils/Urls';

class NameFormComponent extends Component {
    constructor(props) {
        super(props);
        this.sudohandleChange = this.handleChange.bind(this);
        this.state = {
            value: "",
            first_name: "",
            last_name: "",
            middle_name: "",
            suffix: "",
            suffixArray:[]
        };
    }

    handleChange = (e) => {
        const {name, value} = e.target;
        this.setState({[name] : value});

        let  childFamilyData= { nameData :{
            first_name: this.state.first_name,
            last_name: this.state.last_name,
            middle_name: this.state.middle_name,
            suffix : e.target.value}
        };
        this.props.onSelectedChild(childFamilyData);
    };

    componentDidMount = () => {
        let suffixData;
        let uri = API_URL.GET_SUFFIX;
        ajaxGet(uri, (response) => {
            if (response.status === STATUS_ACTIVE) {
                this.setState({
                    suffixArray: response.content,
                });
            }
        });
    };


    render() {
        return (
            <div id="fullname">
                <p><label>First name</label><input onChange={this.handleChange} name="first_name" id="first_name"
                                                   type="text"/></p>
                <p><label>Last name</label><input onChange={this.handleChange} name="last_name" id="last_name"
                                                  type="text"/></p>
                <p><label>Middle name</label><input onChange={this.handleChange} name="middle_name" id="middle_name"
                                                    type="text"/></p>
                <div/>
                <select id="suffix" name="Suffix" onChange={this.handleChange}>
                    <option value="">Select option</option>
                    {this.state.suffixArray.map((value, index) => {
                        return <option value={value.id} key={index}>{value.suffix_type}</option>
                    })  }
                </select>
            </div>
        )
    }
}
export default NameFormComponent;
