/**
 * Created by abhiram on 5/2/20.
 */
import React from 'react';
import {ajaxGet} from '../../Services/Http/Ajax';
import {STATUS_ACTIVE} from '../../Utils/Constants';
import {API_URL} from '../../Utils/Urls';
import '../../App.css';
import {Link} from 'react-router-dom';

const ListUsersComponent = () => {

    const [userList, setUserList] = React.useState([]);

    React.useEffect(() => {
        let uri = API_URL.GET_USER_LIST_URL;
        ajaxGet(uri, (response) => {
            if (response.status === STATUS_ACTIVE) {
                let userData = response.content.map(user => {
                    return {user}
                });
                setUserList(userData);
            }
        });
    }, []);

    return (
        <div className="container">
            <span className="btn">User list </span>
            <div className="table-responsive">
                <table className="table">
                    <thead>
                    <tr>
                        <th>Name</th>
                        <th>Role</th>
                        <th>Action</th>
                    </tr>
                    </thead>
                    <tbody>
                    {userList.map((value, index) => {
                        let editUrl = '/user/' + value.user.id + '/update';
                        let userId = value.user.id;
                        return (
                            <tr key={index}>
                                <td>{value.user.name}</td>
                                <td>{value.user.role}</td>
                                <td>
                                    <Link to={{
                                        pathname: editUrl,
                                        state: {
                                            userId: userId
                                        }
                                    }}>Edit</Link>
                                </td>
                            </tr>
                        )
                    })  }
                    </tbody>
                </table>
            </div>
        </div>
    )
};

export default ListUsersComponent;
