import React from 'react';

const DobFormComponent = (props) => {

    const [dob, setDob] = React.useState('');
    const [age, setAge] = React.useState('');
    const [gender, setGender] = React.useState('');

    const buildDobForm = (e) => {
        e.preventDefault();
        let { name, value } = e.target;
        switch (name) {
            case 'gender':
                setGender(value);
                break;
            case 'dob':
                setDob(value);
                break;
            case 'age':
                setAge(value);
                break;
            default:
                break;
        }

    };

    React.useEffect(() => {
        handleChange();
    }, [dob, age, gender]);

    const handleChange = () => {
        let childFamilyData = {
            dobData: {
                dob: dob,
                age: age,
                gender: gender
            }
        };
        props.onSelectedChild(childFamilyData);
    };

    const getAge=()=>{
        let dobString=dob;
        let today = new Date();
        let birthDate = new Date(dobString);
        let age = today.getFullYear() - birthDate.getFullYear();
        let m = today.getMonth() - birthDate.getMonth();
        if (m < 0 || (m === 0 && today.getDate() < birthDate.getDate())) {
            age--;
        }
        return setAge(age);
    };

    return (
        <div className="label-container">

                <label>DOB</label>
                <input onChange={buildDobForm} onBlur={getAge} name="dob" id="dob" type="date" min='1899-01-01' />

            <p>
                <label>Age</label>
                <input onChange={buildDobForm} value={age} name="age" id="age" type="number" />
            </p>
            <p>Please select your gender:</p>
            <input type="radio" name="gender" value="male" onChange={buildDobForm}/> <span> Male </span>
            <input type="radio" name="gender" value="female" onChange={buildDobForm} /> <span> Female </span>
            <input type="radio" name="gender" value="other" onChange={buildDobForm} /><span>Other</span>
        </div>
    )
};

export default DobFormComponent;
