/**
 * Created by Basil on 6/1/20.
 */
import React, {Component} from 'react';
import '../../App.css';
import Utils from '../../Utils/Util';
import * as constants from '../../Utils/Constants';
import * as urls from '../../Utils/Urls'
import {ajaxPost,ajaxPut} from '../../Services/Http/Ajax';
import Checkbox from "../General/CheckBoxComponent";
import {ToastContainer} from "react-toastify";
class MenuPermissionComponent extends Component {

    constructor(props) {
        super(props);
        this.state = {
            checkedItems: new Map(),
            menuFetched: false,
            subMenuArray: [],
            isMainMenuActive: false
        };
        this.handleChange = this.handleChange.bind(this);
    }

    componentDidUpdate = (prevProps) => {
       this.getMenuPermissionData(prevProps);
    };

    getMenuPermissionData = (prevProps) => {
        if((prevProps.menuId !== this.props.menuId) || prevProps.roleId !== this.props.roleId) {
            let roleId = this.props.roleId;
            let mainMenuId = this.props.menuId;
            if (roleId && mainMenuId) {
                let data = {
                    roleId: roleId,
                    menuId: mainMenuId
                };
                let uri = urls.API_URL.MENU_PERMISSION_URL;
                ajaxPost(uri, data, this.getMenuPermissionDataSuccess)
            }
        }
    };

    getMenuPermissionDataSuccess = (response) => {
        if (response.status === constants.STATUS_ACTIVE) {
            let menuData;
            menuData = response.content;
            let subMenuArray = menuData.map(subMenus => {
                const item = subMenus.sub_menu_id;
                const isChecked = subMenus.is_active === constants.STATUS_ACTIVE;
                this.setState(prevState => ({ checkedItems: prevState.checkedItems.set(item, isChecked) }))
                return {subMenus}
            });
            this.setState({
                subMenuArray: subMenuArray,
                menuFetched: true,
                isMainMenuActive: subMenuArray[0].subMenus.is_active
            });
        }
    };

    putMenuPermissionData = (e) => {
        let putData = {
            data: {
                menu_permission_id: e.target.getAttribute("dataid"),
                is_active: e.target.checked,
                role_id: this.props.roleId,
                sub_menu_id: e.target.id
            }
        };
        let uri = urls.API_URL.MENU_PERMISSION_URL;
        ajaxPut(uri, putData, (response) => {
            if (response.status === constants.STATUS_ACTIVE) {
                Utils.showMessage('success', response.message)
            }
        });
    };

    makeSubMenuCheckBoxDisabled = (e) => {
        this.setState({
            isMainMenuActive: e.target.checked
        });
    };

    handleChange(e) {
        const item = parseInt(e.target.id);
        const isChecked = e.target.checked;
        this.setState(prevState => ({ checkedItems: prevState.checkedItems.set(item, isChecked) }));
        this.putMenuPermissionData(e);
    };

    render() {
        const { menuFetched } = this.state;
        const menuDetails = this.state.subMenuArray;
        return (
            <div>MenuComponent
                <ToastContainer/>
                <div className="menu-permission">
                    {
                        menuFetched && (
                        <div> <b> {menuDetails[0].subMenus.menu_name} <input onChange={this.makeSubMenuCheckBoxDisabled} checked={this.state.isMainMenuActive} type="checkBox" name="menuPermission"/></b>
                            { menuDetails.map( (value, index) => {
                                return <div key={index}>{value.subMenus.sub_menu_name} <Checkbox name={`submenu-${value.subMenus.sub_menu_id}`} dataid={value.subMenus.menu_permission_id} id={value.subMenus.sub_menu_id} checked={this.state.checkedItems.get((value.subMenus.sub_menu_id))} disabled = {!this.state.isMainMenuActive} onChange={this.handleChange} /></div>
                            })  }
                        </div>
                    )}
                </div>
            </div>
        )
    }
}
export default MenuPermissionComponent;
