import React from 'react';
import useForm from '../../Utils/UseForm';

const AddressFormComponent= React.forwardRef((props, ref)=> {

    const [streetAddress, setStreetAddress] = React.useState('');
    const [aptNo, setAptNo] = React.useState('');
    let street_address_form , apt_number_form='';
    const [childFamilyData, setChildFamilyData] = React.useState([]);
    let data='';

    const buildAddressForm = (event) => {
        event.preventDefault();
        let name = event.target.name;
        switch (name) {
            case 'street_address':
                setStreetAddress(event.target.value);
                break;
            case 'apt_no':
                setAptNo(event.target.value);
                break;
            default:
                break;
        }
    };

    const handleChange = () => {
        data = {
            addressData: {
                street_address: streetAddress,
                apt_no: aptNo,
            }
        };

        props.onSelectedChild(data);
    };

    React.useEffect(() => {
        handleChange();
    }, [streetAddress, aptNo]);

    let formDataBuildOne = props.famData ? ((street_address_form=props.famData.address))? props.famData : '' :'';
    let formDataBuildtwo = props.famData ? ((apt_number_form=props.famData.apt_number))? props.famData : '' :'';

    React.useEffect(() => {
        if (street_address_form) {
            setStreetAddress(street_address_form);
            setAptNo(apt_number_form);
        }
    }, [street_address_form]);





    const dataToParent = () => {
        props.onSelectedChild(childFamilyData);
    };

    const { errors, handleErrors } =
        useForm(props, {
            'street_address' : ['required', 'min:5', 'max:20'],
        }, dataToParent);

    React.useImperativeHandle(ref, () => ({

        triggerErrors(){
            handleChange();
            return handleErrors(data.addressData);
        }

    }));



    return (
        <div className="label-container" >
            <label>Address</label><input className="label-container-input" defaultValue={street_address_form} onChange={buildAddressForm} name="street_address" id="street_address"
                                         onBlur={handleErrors}  type="text"/>

            {errors.street_address && (
                <span className="validationError">{errors.street_address}</span>
            )}


            <label className="label-container-apt">Apt No</label><input  className="default-margin-apt"  defaultValue={apt_number_form} onChange={buildAddressForm} name="apt_no" id="apt_no" type="text"/>
        </div>
    )
});

export default AddressFormComponent;
