import React, {Component} from 'react';
import {Redirect} from 'react-router-dom';
import {ajaxPost} from '../../Services/Http/Ajax';
import {STATUS_ACTIVE} from '../../Utils/Constants';
import {API_URL} from '../../Utils/Urls';
import {ControlLabel} from "react-bootstrap";

const HousingTypeFormComponent = (props) => {
    const [homeLessSelect, setHomeLessSelect] = React.useState(false);
    const [housingType, setHousingType] = React.useState([]);

    React.useEffect(() => {
        fetchHousingType(homeLessSelect);
    }, []);

    const handleChange = (e) => {
        let childFamilyData = {
            housingData: {
                housing_type: e.target.value
            }
        };
        props.onSelectedChild(childFamilyData);
    };

    const fetchHousingType = (isChecked) => {
        let uri = API_URL.GET_HOUSINGTYPE;
        let data = {
            housing_type: isChecked
        };
        ajaxPost(uri, data, (response) => {
            if (response.status === STATUS_ACTIVE) {
                setHousingType(response.content);
            }
        });
    };

    const handleHomelessSelect = (e) => {
        setHomeLessSelect(e.target.checked);
        fetchHousingType(e.target.checked);
    };

    return (
        <div className="label-container">
            <label>Homeless</label>
            <input type="checkbox" className="default-margin-homeless"  checked = {homeLessSelect} name="housing_type_checkbox"  onChange={handleHomelessSelect} />
            <label>Housing type</label><select id="housing_type" className="default-margin-address"  name="housing_type" onChange={handleChange} >
                <option value="">Select option</option>
                {
                    housingType.map((value, index) => {
                            return <option key={index} value={value.id}>{value.housing_type}</option>
                        }
                    )
                }
                }
            </select>
        </div>
    )

};

export default HousingTypeFormComponent;
