import React from 'react'

    export const validateEmail = (value) => {
        const validEmailRegex = new RegExp(/^(([^<>()\[\]\.,;:\s@\"]+(\.[^<>()\[\]\.,;:\s@\"]+)*)|(\".+\"))@(([^<>()[\]\.,;:\s@\"]+\.)+[^<>()[\]\.,;:\s@\"]{2,})$/i);
        let errors_email = validEmailRegex.test(value);
        let valid = false;
        if(errors_email === true) {
            valid =true;
        }
        return valid;
    };

    export const validatePassword = (value) => {
        const strongRegex = new RegExp("^(?=.*[A-Za-z])(?=.*\d)[A-Za-z\d]{6,}$");
        let errors_password = strongRegex.test(value);
        let valid = false;
        if(errors_password === true ) {
            valid = true;
        }
        return true;
    };
