import React,{useState,useEffect} from 'react'
import useForm from '../../Utils/UseForm';

const ImageUploadComponent = React.forwardRef((props, ref) => {

    const [image,setImage] = useState('');

    useEffect(() => {
        if (props.isUpdateDataFromParent)
        {
            setImage(props.imageDataFromParent)
        }
    },[props.imageDataFromParent]);

    const handleChange = (e) => {
        let files = e.target.files;
        let reader = new FileReader();
        reader.readAsDataURL(files[0]);
        reader.onload = (e) => {
            props.onSelectedImage(e.target.result,files[0],files[0].type);
            setImage(e.target.result);
        };
    };

    return(
        <div className="form-group">
            <label className="control-label">Logo</label>
            <div className="controls">
                <input type="file" name="logo" id="logo" onChange={handleChange} />
                {/*{errors.logo && (*/}
                    {/*<span className="validationError">{errors.logo}</span>*/}
                {/*)}*/}
            </div>
            <img src={image} />
        </div>
    )
});
export default ImageUploadComponent
