/**
 * Created by akshay on 20/1/20.
 */
import React, {Component} from 'react';
import '../../App.css';
import {Link} from 'react-router-dom';
import Jumbotron from 'react-bootstrap/Jumbotron';
import {RENDER_URL} from '../../Utils/Urls';
import Card from 'react-bootstrap/Card';

const AreaComponent = () => {

    const [value, setValue] = React.useState('');
    const [currentDate, setCurrentDate] = React.useState('');

    React.useEffect(() => {
        let today = new Date();
        let dd = String(today.getDate()).padStart(2, '0');
        let mm = String(today.getMonth() + 1).padStart(2, '0'); //January is 0!
        let yyyy = today.getFullYear();
        today = mm + '/' + dd + '/' + yyyy;
        return setCurrentDate(today);
    }, []);

    return (

        <div>
            <Jumbotron>
                <h4>Serve Families on {currentDate}</h4>
                <p>
                    <span>
                          <Link to={RENDER_URL.ADD_FAMILY_URL} className="btn btn-secondary" variant="secondary">Food Pantry</Link>
                        <br/>
                    </span>
                    <span>
                        <br/>
                           <Link to={RENDER_URL.ADD_FAMILY_URL} className="btn btn-secondary" variant="secondary">Holiday Basket</Link>
                    </span>
                </p>
                <p>
                </p>
            </Jumbotron>
        </div>
    )
};

export default AreaComponent ;
