import React, {Component} from 'react';
import {Redirect} from 'react-router-dom';
import Utils from '../../Utils/Util';
import {ajaxGet} from '../../Services/Http/Ajax';
import {STATUS_ACTIVE} from '../../Utils/Constants';
import {API_URL} from '../../Utils/Urls';
import Button from 'react-bootstrap/Button';

const LogoutComponent = () => {

    const [isLogoutSuccess, setIsLogoutSuccess] = React.useState(false);

    const handleLogout = () => {
            let uri = API_URL.LOGOUT_URL;
            ajaxGet(uri, (response) => {
                if (response.status === STATUS_ACTIVE) {
                    Utils.clearItemsFromLocalStorage();
                    setIsLogoutSuccess(true);
                }
            });
        };

    return (
        <div>
            {isLogoutSuccess && (<Redirect to={'/'}/>)}
            <Button className ="log-out-btn btn" variant="info" onClick={handleLogout} size="sm">Logout</Button>
        </div>
    )
};

export default LogoutComponent;
