import React from 'react';

const ContactNumberFormComponent = (props) => {

    const [primaryNumber, setPrimaryNumber] = React.useState('');
    const [secondaryNumber, setSecondaryNumber] = React.useState('');

    const buildContactForm = (e) => {
        e.preventDefault();
        let { name, value } = e.target;
        switch (name) {
            case 'primary_number':
                setPrimaryNumber(value);
                break;
            case 'secondary_number':
                setSecondaryNumber(value);
                break;
            default:
                break;
        }
    };

    React.useEffect(() => {
        handleChange();
    }, [primaryNumber, secondaryNumber]);

    const handleChange = () => {
        let childFamilyData = {
            contactData: {
                primaryNumber: primaryNumber,
                secondaryNumber: secondaryNumber
            }
        };
        props.onSelectedChild(childFamilyData);
    };

    return (
        <div className="label-container">
            <label className="label-container-primary">Primary number</label><input onChange={buildContactForm} name="primary_number" id="primary_number"
                                                   value={primaryNumber} type="text"/>
            <label>Secondary number</label><input onChange={buildContactForm} name="secondary_number"
                                                     value={secondaryNumber} id="secondary_number" type="text"/>
        </div>
    )
};

export default ContactNumberFormComponent;
