/**
 * Created by abhiram on 7/1/20.
 */

import React, {Component} from 'react';
import Utils from '../../Utils/Util';

class MenuComponent extends Component {
    constructor(props) {
        super(props);
        this.permissions = JSON.parse(Utils.getItemLocalStorage('menu_permission'));
        this.permissionArray = Object.keys(this.permissions).map(i => this.permissions[i]);
    }

    render() {
        return (
            <div className="header">
                <a href="#default" className="logo">Fresh Track</a>

                <div className="header-right">
                    <button href="#" className="btn btn-primary  btn-block">logout</button>
                </div>
                <div className="header-right">
                    <Link to="/menupermission" >menu permission</Link>
                </div>
                <div className="list-group">
                    <ul>
                        { this.permissionArray.map((value, index) => {
                            return (
                                <div key={index}>
                                    <span><u>{value[0].menu_name}</u></span>
                                    {
                                        value.map((valueInner, indexInner) => {
                                            return (
                                                <table key={indexInner}>
                                                    <thead>
                                                    <tr>
                                                    </tr>
                                                    </thead>
                                                    <tbody>
                                                    <tr>
                                                        <td>{valueInner.sub_menu_name}</td>
                                                    </tr>
                                                    </tbody>
                                                </table>
                                            )})
                                    }
                                    <br/>
                                </div>
                            )
                        })}
                    </ul>
                </div>
            </div>
        )
    }
}
export default MenuComponent;
