import React from 'react';
import PropTypes from 'prop-types';

const Checkbox = ({ type = 'checkbox', name, dataid = null, id, checked = 0, disabled, onCheckedChange }) => {
    const [checkedItem, setCheckedItem] = React.useState(checked);
    const changeCheckedInput = (e) => {
        setCheckedItem(!checked);
        onCheckedChange(e);
    };
    React.useEffect(() => {
        setCheckedItem(checked)
    },[checked]);
    return <input type={type} name={name} dataid={dataid} id={id} checked={checkedItem} disabled={disabled}
           onChange={changeCheckedInput}/>;
};

Checkbox.propTypes = {
    type: PropTypes.string,
    name: PropTypes.string.isRequired,
    id: PropTypes.number,
    dataid: PropTypes.number,
    checked: PropTypes.bool,
    disabled: PropTypes.bool,
    onChange: PropTypes.func.isRequired,
};

export default Checkbox;
