/**
 * Created by abhiram on 5/2/20.
 */
import React from 'react';
import {ajaxGet, ajaxDestroy} from '../../Services/Http/Ajax';
import {STATUS_ACTIVE} from '../../Utils/Constants';
import {confirm, showMessage} from '../../Utils/Util';
import {API_URL} from '../../Utils/Urls';
import '../../App.css';
import {ToastContainer} from "react-toastify";
import {Link} from 'react-router-dom';

const ListUsersComponent = () => {

    const [userList, setUserList] = React.useState([]);
    const [isUserDeleted, setIsUserDeleted] = React.useState('');

    React.useEffect(() => {
        let uri = API_URL.GET_USER_LIST_URL;
        ajaxGet(uri, (response) => {
            if (response.status === STATUS_ACTIVE) {
                setUserList(response.content);
            }
        });
    }, [isUserDeleted]);

    const deleteUserConfirm = (e) => {
        e.preventDefault();
        let title = 'Are you sure you wan to delete the user?';
        let userId = e.target.value;
        confirm(title, deleteUser, userId);
    };

    const deleteUser = (confirm, userId) => {
      let uri = API_URL.GET_USER_DATA_URL + userId;
      ajaxDestroy(uri, (response) => {
          setIsUserDeleted(!isUserDeleted);
          showMessage('success', response.message);
      })
    };

    return (
        <div className="container">
            <ToastContainer/>
            <span className="btn">User list </span>
            <div className="table-responsive">
                <table className="table table-striped table-responsive-md btn-table">
                    <thead>
                    <tr>
                        <th>Name</th>
                        <th>Role</th>
                        <th>Action</th>
                    </tr>
                    </thead>
                    <tbody>
                    {userList.map((value, index) => {
                        let editUrl = '/user/' + value.id + '/update';
                        let userId = value.id;
                        return (
                            <tr key={index}>
                                <td>{value.name}</td>
                                <td>{value.role}</td>
                                <td>
                                    <Link className="btn btn-secondary btn-sm m-0" to={{
                                        pathname: editUrl,
                                        state: {
                                            userId: userId
                                        }
                                    }}>Edit</Link>
                                    <button className="btn btn-secondary btn-sm m-0" value={userId} onClick={deleteUserConfirm} > Delete </button>
                                </td>
                            </tr>
                        )
                    })  }
                    </tbody>
                </table>
            </div>
        </div>
    )
};

export default ListUsersComponent;
