import React,{ useState, useEffect } from 'react';
import * as urls from "../../Utils/Urls";
import * as constants from '../../Utils/Constants';
import {ajaxGet, ajaxPut} from "../../Services/Http/Ajax";
import {STATUS_ACTIVE} from "../../Utils/Constants";
import Utils from '../../Utils/Util';

const AgencyUpdateComponent = (props) => {

    const [name,setName] = useState('');
    const [address,setAddress] = useState('');
    const [id, setId] = useState(props.location.state);

    useEffect(() => {
        let agencyData;
        let agencyId = id.agencyId;
        let uri = urls.API_URL.EDITED_URL + '/' + agencyId + '/update';
        ajaxGet(uri, (response) => {
            if (response.status === constants.STATUS_ACTIVE) {
                agencyData = response.content;
                let agencyArray = agencyData.map(agency => {
                    return {agency}
                });
                setName(agencyArray[0].agency.name);
                setAddress(agencyArray[0].agency.address1);
            }
        })
    }, []);

    const createFamilySuccess = (response) => {
        if(response.status === STATUS_ACTIVE) {
            Utils.showMessage('success', response.message);
        }
    };

    const updateDetails = () => {
        let payLoad = {
            name: name,
            address: address
        };
        let agencyId = id.agencyId;
        let uri = urls.API_URL.EDITED_URL + '/' + agencyId + '/update';
        ajaxPut(uri, payLoad, createFamilySuccess)
    };

    const handleChange = (e) => {
        e.preventDefault();
        let name = e.target.name;
        switch (name) {
            case 'name':
                setName(e.target.value);
                break;
            case 'address':
                setAddress(e.target.value);
                break;
            default:
                break;
        }
    };

    return(
        <div>
            <div className="control-group">
                <div className="controls">
                        <div>
                            <label className="control-label" >Agency Name : </label>
                            <input type="text" id="agencyName" name="name" onChange={handleChange} className="input-xlarge" value={name}/>
                        </div>
                        <div>
                            <label className="control-label" >Address : </label>
                            <input type="text" id="agencyName" name="address" onChange={handleChange} className="input-xlarge" value={address}/>
                        </div>
                        <div className="control-group help-block">
                            <div className="controls">
                                <button type="submit" className="btn btn-success" onClick={updateDetails}>Update</button>
                            </div>
                        </div>
                    </div>
            </div>
        </div>
    )
};

export default AgencyUpdateComponent
