/**
 * Created by akshay on 22/1/20.
 */
import React, {Component} from 'react';
import {ajaxGet} from '../../Services/Http/Ajax';
import * as constants from '../../Utils/Constants'
import * as urls from '../../Utils/Urls'
import '../../App.css';
import {DropdownButton, Dropdown} from "react-bootstrap";

class RoleSelectionComponent extends Component {

    constructor(props) {
        super(props);
        this.state = {
            roleValue: [],
            selectedRole: null
        };
        this.handleChangeInRole = this.handleChangeInRole.bind(this);
    };

    componentDidMount = () => {
        let roleData;
        let uri = urls.API_URL.GET_ROLE_LIST_URL;
        ajaxGet(uri, (response) => {
            if (response.status === constants.STATUS_ACTIVE) {
                roleData = response.content;
                let roleArray = roleData.map(roles => {
                    return {roles}
                });
                this.setState({
                    roleValue: roleArray,
                });
            }
        });
    };

    handleChangeInRole = (e) => {
        this.setState({
            selectedRole: e.target.value
        });
        this.props.onSelectedRole(e.target.value);
    };

    render() {
        return (
            <div className="role-data">
               {/* <span className="btn">Role: </span>
                <select className="dropdown-toggle btn btn-secondary" onChange={this.handleChangeInRole}>
                    <option className="dropdown-item" value="">Select Options</option>
                    {this.state.roleValue.map( (value, index) => {
                        return <option className="dropdown-item" value={value.roles.id} key={index}>{value.roles.role_name}</option>
                    })  }
                </select>*/}
                <DropdownButton
                    as={InputGroup.Prepend}
                    variant="outline-secondary"
                    title="Dropdown"
                    id="input-group-dropdown-1"
                >
                    <Dropdown.Item href="#">Action</Dropdown.Item>
                    <Dropdown.Item href="#">Another action</Dropdown.Item>
                    <Dropdown.Item href="#">Something else here</Dropdown.Item>
                    <Dropdown.Divider />
                    <Dropdown.Item href="#">Separated link</Dropdown.Item>
                </DropdownButton>
            </div>
        )
    }
}
export default RoleSelectionComponent ;
