/**
 * Created by abhiram on 6/1/20.
 */
import React, {Component} from 'react';
import * as constants from '../../Utils/Constants'
import * as urls from '../../Utils/Urls'
import {ajaxGet} from '../../Services/Http/Ajax';
import '../../App.css';
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';
import {Link} from "react-router-dom";
import MenuComponent from '../General/MenuComponent';
import FooterComponent from '../General/FooterComponent';

const ListFamilyContainer = () => {
    const [familyArray, setFamilyArray] = React.useState([]);

    React.useEffect(() => {
        let familyData;
        let uri = urls.API_URL.GET_FAMILY_LIST;
        ajaxGet(uri, (response) => {
            if (response.status === constants.STATUS_ACTIVE) {
                familyData = response.content;
                let familyArray = familyData.map(family => {
                    return {family}
                });
                setFamilyArray(familyArray);
            }
        });
    }, []);

    return (
        <div>
            <MenuComponent/>
            <Card>
                <Card.Header>Family list</Card.Header>
                <Card.Body>
                    <table className="table">
                        <thead>
                        <tr>
                            <th>Slno</th>
                            <th>First Name</th>
                            <th>Last Name</th>
                            <th>Middle Name</th>
                            <th>Actions</th>
                        </tr>
                        </thead>
                        <tbody>
                        {familyArray.map((value, index) => {
                            let url = '/family/' + value.family.id + '/update';
                            let familyId = value.family.id;
                            return (
                                <tr key={index}>
                                    <td>{value.family.id}</td>
                                    <td>{value.family.first_name}</td>
                                    <td>{value.family.last_name}</td>
                                    <td>{value.family.middle_name}</td>
                                    <td>
                                        <Link to={{
                                            pathname: url,
                                            state: {
                                                familyId: familyId
                                            }
                                        }}>Edit</Link>
                                    </td>
                                </tr>
                            )
                        })  }
                        </tbody>
                    </table>
                </Card.Body>
            </Card>
            <FooterComponent/>
        </div>

    )
};
export default ListFamilyContainer;
