/**
 * Created by abhiram on 6/1/20.
 */
import React, {Component} from 'react';
import * as constants from '../../Utils/Constants'
import * as urls from '../../Utils/Urls'
import {ajaxGet} from '../../Services/Http/Ajax';
import '../../App.css';
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';
import {Link} from "react-router-dom";
import MenuComponent from '../General/MenuComponent';
import FooterComponent from '../General/FooterComponent';

const ListFamilyContainer = () => {
    const [familyArray, setFamilyArray] = React.useState([]);

    React.useEffect(() => {
        let familyData;
        let uri = urls.API_URL.GET_FAMILY_LIST;
        ajaxGet(uri, (response) => {
            if (response.status === constants.STATUS_ACTIVE) {
                familyData = response.content;
                let familyArray = familyData.map(family => {
                    return {family}
                });
                setFamilyArray(familyArray);
            }
        });
    }, []);

    return (
        <div>
            <MenuComponent/>

            <Card>
                <Card.Header>Family list</Card.Header>
                <Card.Body>
                    <table>
                        {familyArray.map( (value, index) => {
                            let url = '/family/update/'+ value.family.family_id+'?';
                            return(
                                <table>
                                    <tr>
                                        <th>family_id</th>
                                        <th>Firstname</th>
                                        <th>Lastname</th>
                                        <th>middlename</th>
                                    </tr>
                                    <tr>
                                        <td key={index} >{value.family.family_id}</td>
                                        <td key={index} >{value.family.first_name}</td>
                                        <td key={index}>{value.family.last_name}</td>
                                        <td key={index}>{value.family.middle_name}</td>
                                        <td key={index}>
                                            <Link to={url}> Edit </Link>

                                        </td>
                                    </tr>
                                </table>
                            )
                        })  }
                    </table>
                </Card.Body>
            </Card>
            <FooterComponent/>
        </div>

    )
};
export default ListFamilyContainer;
