import React, {Component} from 'react';
import {Redirect} from 'react-router-dom';
import {ajaxGet} from '../../Services/Http/Ajax';
import {STATUS_ACTIVE} from '../../Utils/Constants';
import {API_URL} from '../../Utils/Urls';
import useForm from '../../Utils/UseForm';


const NameFormComponent =  React.forwardRef((props, ref) => {
    const [firstName, setFirstName] = React.useState('');
    const [lastName, setLastName] = React.useState('');
    const [middleName, setMiddleName] = React.useState('');
    const [suffix, setSuffix] = React.useState('');
    const [suffixArray, setSuffixArray] = React.useState([]);
    let first_name,last_name,middle_name,suffix_form_data='';
    let middle_name_form,first_name_form,last_name_form='';
    const [childFamilyData, setChildFamilyData] = React.useState([]);
    let data = '';

    React.useEffect(() => {
        let uri = API_URL.GET_SUFFIX;
        ajaxGet(uri, (response) => {
            if (response.status === STATUS_ACTIVE) {
                setSuffixArray(response.content);
            }

        });
    }, []);


    const buildNameForm = (e) => {
        let { name, value } = e.target;
        let setFunction = '';
      switch (name) {
          case 'first_name':
              setFunction = setFirstName;
              break;
          case 'last_name':
              setFunction = setLastName;
              break;
          case 'middle_name':
              setFunction = setMiddleName;
              break;
          case 'Suffix':
              setFunction = setSuffix;
              break;
          default:
              break;
      }

      if (setFunction !== '') {
          setFunction(value)
      }
    };

    React.useEffect(() => {
        if (first_name) {
            setFirstName(first_name);
            setMiddleName(middle_name);
            setLastName(last_name);
            setSuffix(suffix_form_data);

        }
        handleChange();
    }, [firstName, lastName,middleName, suffix]);



    const handleChange = () => {


        data = { nameData :{
            first_name: firstName,
            last_name: lastName,
            middle_name: middleName,
            suffix : suffix
        }
        };
         props.onSelectedChild(data);
    };


    let formDataBuildOne = props.famData ? ((first_name_form= props.famData.first_name) )? props.famData : '' :'';
    let formDataBuildtwo = props.famData ? ((middle_name_form=props.famData.middle_name))? props.famData : '' :'';
    let formDataBuildthree = props.famData ? ((last_name_form=props.famData.last_name))? props.famData : '' :'';
    let formDataBuildfour = props.famData ? ((suffix_form_data=props.famData.suffix))? props.famData : '' :'';



    React.useEffect(() => {
        if (first_name_form) {
            setFirstName(first_name_form);
            setMiddleName(middle_name_form);
            setLastName(last_name_form);
            setSuffix(suffix_form_data);
        }
    }, [first_name_form, middle_name_form, last_name_form, suffix_form_data]);

    React.useEffect(() => {
        if (first_name_form) {
            setFirstName(first_name);
            setMiddleName(middle_name);
            setLastName(last_name);
            setSuffix(suffix_form_data);
        }
    }, []);




    const dataToParent = () => {
        props.onSelectedChild(childFamilyData);
    };

    const { errors, handleErrors } =
        useForm(props, {
            'first_name' : ['required', 'min:1'],
            'last_name' : ['required', 'min:1']
        }, dataToParent);

    React.useImperativeHandle(ref, () => ({

        triggerErrors(){
            handleChange();
            return handleErrors(data.nameData);
        }

    }));


    return (
        <div id="fullname">
            <div className="label-container">

            <label>First name</label><input className="default-margin-name"  onChange={buildNameForm} name="first_name" id="first_name"
                                             onBlur={handleErrors} type="text" defaultValue={first_name_form} />

                {errors.first_name && (
                    <span className="validationError">{errors.first_name}</span>
                )}

            <label>Last name</label><input  className="label-container-lst" onChange={buildNameForm} name="last_name" id="last_name"
                                            onBlur={handleErrors}  type="text" defaultValue={last_name_form}  />
                {errors.last_name && (
                    <span className="validationError">{errors.last_name}</span>
                )}

            <label>Middle name</label><input onChange={buildNameForm} defaultValue={middle_name_form}  name="middle_name" id="middle_name"
                                                type="text"/>
            <div/>
                <div className="label-container">
                    <label>Suffix</label>

                    <select value={suffix}  id="suffix" name="Suffix"  className="default-margin"  onChange={buildNameForm}>

                <option  >Select option</option>
                {suffixArray.map((value, index) => {
                    return <option value={value.suffix_type}    key={index}>{value.suffix_type}</option>
                })  }
            </select>
                </div>
            </div>
        </div>
    )
});

export default NameFormComponent;
