/**
 * Created by abhiram on 5/2/20.
 */

import React from 'react';
import MenuComponent from '../General/MenuComponent';
import FooterComponent from '../General/FooterComponent';
import '../../App.css';
import RoleSelectionComponent from '../General/RoleSelectionComponent';
import {validateEmail, validatePassword} from '../../Utils/Validation';
import {ajaxPost, ajaxGet, ajaxPut} from '../../Services/Http/Ajax';
import {STATUS_ACTIVE} from '../../Utils/Constants'
import {API_URL} from '../../Utils/Urls';
import Utils from '../../Utils/Util';
import {ToastContainer} from "react-toastify";
let isEmailValid = false; let isPasswordValid = false; let isConfirmPasswordValid = false;

const CreateEditUsersContainer = (props) => {

    const [role, setRole] = React.useState('');
    const [name, setName] = React.useState('');
    const [email, setEmail] = React.useState('');
    const [password, setPassword] = React.useState('');
    const [confirmPassword, setConfirmPassword] = React.useState('');
    const [emailError, setEmailError] = React.useState('');
    const [passwordError, setPasswordError] = React.useState('');
    const [confirmPasswordError, setConfirmPasswordError] = React.useState('');
    let isUserUpdate = !!props.location.state;
    let buttonLabel = isUserUpdate ? 'Update': 'Register';

    const handleChange = (event) => {
        event.preventDefault();
        let name = event.target.name;
        switch (name) {
            case 'email':
                setEmail(event.target.value);
                let emailErrorValue = validateEmail(event.target.value) ? isEmailValid = true : 'Please enter a valid email';
                setEmailError(emailErrorValue);
                break;
            case 'password':
                setPassword(event.target.value);
                let passwordErrorValue = validatePassword(event.target.value) ? isPasswordValid = true : 'Please enter the password';
                setPasswordError(passwordErrorValue);
                break;
            case 'username':
                setName(event.target.value);
                break;
            case 'password_confirm':
                setConfirmPassword(event.target.value);
                let confirmPasswordError = validateConfirmPassword(event.target.value) ? isConfirmPasswordValid = true : 'Password should match';
                setConfirmPasswordError(confirmPasswordError);
                break;
            default:
                break;
        }
    };

    const validateConfirmPassword = (confirmPasswordContent) => {
        let confirmPasswordValid = true;
        if(confirmPasswordContent !== password) {
            confirmPasswordValid =  false;
        }
        return confirmPasswordValid;
    };

    const handleChangeInRole = (roleId) => {
        setRole(roleId);
    };

    const createFamily = (e) => {
        e.preventDefault();
        if(isUserUpdate) {
            updateFamily()
        } else {
            let data = {
                data: {
                    name: name,
                    email: email,
                    password: password,
                    role_id: role
                }
            };
            let uri = API_URL.CREATE_USER_URL;
            ajaxPost(uri, data, createFamilySuccess)
        }
    };

    const updateFamily = () => {
        let data = {
            data: {
                name: name,
                email: email,
                user_id: props.location.state.userId,
                role_id: role
            }
        };
        let uri = API_URL.UPDATE_USER_DATA_URL;
        ajaxPut(uri, data, createFamilySuccess)
    };

    const createFamilySuccess = (response) => {
        if(response.status === STATUS_ACTIVE) {
            Utils.showMessage('success', response.message);
        }
    };

    React.useEffect(() => {
        if(isUserUpdate) {
            let uri = API_URL.GET_USER_DATA_URL + props.location.state.userId;
            ajaxGet(uri, (response) => {
                if (response.status === STATUS_ACTIVE) {
                    setName(response.content[0].name);
                    setEmail(response.content[0].email);
                    setRole(response.content[0].role_id);
                }
            });
        }
    }, []);

    return (
        <div>
            <MenuComponent/>
            <ToastContainer/>
            <form className="form-horizontal" onSubmit={createFamily}>
                <fieldset>
                    <div id="legend">
                        <legend className="">Register</legend>
                    </div>
                    <div className="control-group">
                        <label className="control-label" >Username</label>
                        <div className="controls">
                            <input type="text" id="username" name="username" onChange={handleChange} className="input-xlarge" value={name}/>
                        </div>
                    </div>

                    <div className="control-group">
                        <label className="control-label">E-mail</label>
                        <div className="controls">
                            <input type="text" id="email" name="email" onChange={handleChange} className="input-xlarge" value={email}/>
                            {<span className='validationError'>{emailError}</span>}
                        </div>
                    </div>

                    {!isUserUpdate &&
                    (<div className="control-group">
                        <label className="control-label">Password</label>
                        <div className="controls">
                            <input type="password" id="password" name="password" onBlur={handleChange}
                                   className="input-xlarge"/>
                            {<span className='validationError'>{passwordError}</span>}
                        </div>
                    </div>)
                    }

                    {!isUserUpdate &&
                    (<div className="control-group">
                        <label className="control-label">Password (Confirm)</label>
                        <div className="controls">
                            <input type="password" id="password_confirm" name="password_confirm" onBlur={handleChange}
                                   className="input-xlarge"/>
                            {<span className='validationError'>{confirmPasswordError}</span>}
                            <p className="help-block"></p>
                        </div>
                    </div>)
                    }
                    <div className="control-group">
                        <RoleSelectionComponent onSelectedRole = {handleChangeInRole} roleId={role}/>
                    </div>

                    <div className="control-group help-block">
                        <div className="controls">
                            <button type="submit" className="btn btn-success">{buttonLabel}</button>
                        </div>
                    </div>
                </fieldset>
            </form>
            <FooterComponent/>
        </div>
    )

};

export default CreateEditUsersContainer;
