/**
 * Created by Basil on 6/1/20.
 */
import React, {Component} from 'react';
import MenuComponent from '../General/MenuComponent';
import FooterComponent from '../General/FooterComponent';
import RoleSelectionComponent from '../General/RoleSelectionComponent';
import MenuPermissionComponent from '../MenuPermission/MenuPermissionComponent';
import '../../App.css';
import MenuListComponent from "../General/MenuListComponent";
import AgencySelectionComponent from "../General/AgencySelectionComponent"

class MenuPermissionContainer extends Component {

    constructor(props) {
        super(props);
        this.state = { role: '', mainMenu: '',agency: '' }
    }

    handleChangeInRole = (roleId) => {
        this.setState({role: roleId});
    };

    handleChangeInMainMenu = (mainMenuId) => {
        this.setState({mainMenu: mainMenuId});
    };
    handleChangeInAgency = (agencyId) => {
        this.setState({agency: agencyId});
    };

    render() {
        return (
            <div>
                <MenuComponent/>
                <div className="main-container">
                    <div className="data-container">
                        <RoleSelectionComponent onSelectedRole = {this.handleChangeInRole}/>
                        <MenuListComponent onSelectedMainMenu = {this.handleChangeInMainMenu}/>
                        {/*<AgencySelectionComponent onSelectedAgency = {this.handleChangeInAgency}/>*/}
                    </div>
                    <div>
                        <MenuPermissionComponent menuId = {this.state.mainMenu} roleId = {this.state.role} />
                    </div>
                </div>
                <FooterComponent/>
            </div>
        )
    }
}
export default MenuPermissionContainer;
