import React from 'react';
import useForm from '../../Utils/UseForm';


const ContactNumberFormComponent = React.forwardRef((props, ref) => {

    const [primaryNumber, setPrimaryNumber] = React.useState('');
    const [secondaryNumber, setSecondaryNumber] = React.useState('');
    let primary_number_form,secondary_number_form='';
    const [childFamilyData, setChildFamilyData] = React.useState([]);
    let data = '';

    const buildContactForm = (e) => {
        e.preventDefault();
        let { name, value } = e.target;
        switch (name) {
            case 'primary_number':
                setPrimaryNumber(value);
                break;
            case 'secondary_number':
                setSecondaryNumber(value);
                break;
            default:
                break;
        }
    };

    React.useEffect(() => {
        handleChange();
    }, [primaryNumber, secondaryNumber]);

    const handleChange = () => {
        data = {
            contactData: {
                primary_number: primaryNumber,
                secondary_number: secondaryNumber
            }
        };
        props.onSelectedChild(data);
    };


    let formDataBuildOne = props.famData ? ((primary_number_form=props.famData.primary_contact) && (secondary_number_form=props.famData.secondary_contact))? props.famData : '' :'';


    React.useEffect(() => {
        if (primary_number_form) {
            setPrimaryNumber(primary_number_form);
            setSecondaryNumber(secondary_number_form);

        }
    }, [primary_number_form]);





    const dataToParent = () => {
        props.onSelectedChild(childFamilyData);
    };

    const { errors, handleErrors } =
        useForm(props, {
            'primary_number' : ['required', 'min:2', 'max:12'],
        }, dataToParent);

    React.useImperativeHandle(ref, () => ({

        triggerErrors(){
            handleChange();
            return handleErrors(data.contactData);
        }

    }));




    return (
        <div className="label-container">
            <label>Primary number</label><input  defaultValue={primary_number_form} onChange={buildContactForm} name="primary_number" id="primary_number"
                                                 onBlur={handleErrors}  type="text"/>

            {errors.primary_number && (
                <span className="validationError">{errors.primary_number}</span>
            )}

            <label className="label-container-secondarynumber"> Secondary number</label><input defaultValue={secondary_number_form} onChange={buildContactForm} name="secondary_number"
                                                      id="secondary_number" type="text"/>
        </div>
    )
});

export default ContactNumberFormComponent;
