/**
 * Created by Basil on 03/02/2020.
 */
import React, {Component} from 'react';
import {ajaxGet} from '../../Services/Http/Ajax';
import * as constants from '../../Utils/Constants'
import * as urls from '../../Utils/Urls'
import '../../App.css';

class AgencySelectionComponent extends Component {

    constructor(props) {
        super(props);
        this.state = {
            agencyValue: [],
            selectedAgency: null
        };
        this.handleChangeInAgency = this.handleChangeInAgency.bind(this);
    };

    componentDidMount = () => {
        let agencyData;
        let uri = urls.API_URL.GET_AGENCY_LIST_URL;
        ajaxGet(uri, (response) => {
            if (response.status === constants.STATUS_ACTIVE) {
                agencyData = response.content;
                let agencyArray = agencyData.map(agency => {
                    return {agency}
                });
                this.setState({
                    agencyValue: agencyArray,
                });
            }
        });
    };

    handleChangeInAgency = (e) => {
        this.setState({
            selectedAgency: e.target.value
        });
        this.props.onSelectedAgency(e.target.value);
    };

    render() {
        return (
            <div className="agency-data">
                <span className="btn">Agency: </span>
                <select className="dropdown-toggle btn btn-secondary" onChange={this.handleChangeInAgency}>
                    <option className="dropdown-item" value="">Select Options</option>
                    {this.state.agencyValue.map( (value, index) => {
                        return <option className="dropdown-item" value={value.agency.id} key={index}>{value.agency.role_name}</option>
                    })  }
                </select>
            </div>
        )
    }
}
export default AgencySelectionComponent;
