import React from 'react'
import {toast} from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

class Util {

    constructor() {
        this.url = 'http://localhost:8000';
        this.options = {
            hideProgressBar: true,
            position: toast.POSITION.TOP_RIGHT,
            autoClose: 500,
            enter: "zoomIn",
            hideEasing: "linear",
            showMethod: "fadeIn",
            hideMethod: "fadeOut"
        };
    }

    showMessage(type, message) {
        switch (type) {
            case 'success' :
                toast.success(message, this.options);
                break;
            case 'error' :
                toast.error(message, this.options);
                break;
            default :
                toast.warn(message, this.options);
                break;
        }
    }

    setItemToLocalStorage(storageKey, storageValue) {
        return localStorage.setItem(storageKey, JSON.stringify(storageValue));
    }

    getItemLocalStorage(storageKey) {
        let key = "" + storageKey;
        return localStorage.getItem(key);
    }

    clearItemsFromLocalStorage() {
        return localStorage.clear();
    }
}
export default (new Util);
