import React, {Component} from 'react';
import {Redirect,Link} from 'react-router-dom';
import Utils from '../../Utils/Util';
import {ajaxGet} from '../../Services/Http/Ajax';
import * as constants from '../../Utils/Constants';
import * as urls from '../../Utils/Urls';

class LogoutComponent extends Component {

    constructor(props) {
        super(props);
        this.handleLogout = this.handleLogout.bind(this);
        this.state = {
            isLogoutSuccess: false
        }
    }

    handleLogout = () => {
        let uri = urls.API_URL.LOGOUT_URL;
        ajaxGet(uri, (response) => {
            if (response.status === constants.STATUS_ACTIVE) {
                Utils.clearItemsFromLocalStorage();
                this.setState({isLogoutSuccess: true})
            }
        });
    };

    render() {
        const { isLogoutSuccess } = this.state;
        return (
            <div>
                {isLogoutSuccess && (<Redirect to={'/'}/>)}
                <Link className ="log-out-btn btn-primary" onClick={this.handleLogout} >Logout</Link>
            </div>
        )
    }
}
export default LogoutComponent;
