/**
 * Created by akshay on 22/1/20.
 */
import React from 'react';
import {ajaxGet} from '../../Services/Http/Ajax';
import {STATUS_ACTIVE} from '../../Utils/Constants';
import {API_URL} from '../../Utils/Urls';
import '../../App.css';

const RoleSelectionComponent = (props) => {

    const [roleValue, setRoleValue] = React.useState([]);
    const [selectedRole, setSelectedRole] = React.useState(null);

    const [permission,setPermission]= React.useState({});
    const handleChangeInRole = (e) => {
        setSelectedRole(e.target.value);
        setPermission({id:e.target.value});
        console.log(permission);
        props.onSelectedRole(e.target.value);
    };

    React.useEffect(() => {
        let uri = API_URL.GET_ROLE_LIST_URL;
        ajaxGet(uri, (response) => {
            if (response.status === STATUS_ACTIVE) {
                setRoleValue(response.content);
            }
        });
    }, []);

    return (
        <div className="role-data">
            <span className="btn">Role: </span>
            <select value={props.roleId} className="dropdown-toggle btn btn-secondary" onChange={handleChangeInRole}>
                <option className="dropdown-item" value="">Select Options</option>
                {roleValue.map( (value, index) => {
                    return <option className="dropdown-item" value={value.id} key={index}>{value.role_name}</option>
                })  }
            </select>
        </div>
    )
};

export default RoleSelectionComponent ;
