import React from 'react';

const AddressFormComponent = (props) => {

    const [streetAddress, setStreetAddress] = React.useState('');
    const [aptNo, setAptNo] = React.useState('');

    const buildAddressForm = (event) => {
        event.preventDefault();
        let name = event.target.name;
        switch (name) {
            case 'street_address':
                setStreetAddress(event.target.value);
                break;
            case 'apt_no':
                setAptNo(event.target.value);
                break;
            default:
                break;
        }
    };

    const handleChange = () => {
        let childFamilyData = {
            addressData: {
                streetAddress: streetAddress,
                aptNo: aptNo,
            }
        };

        props.onSelectedChild(childFamilyData);
    };

    React.useEffect(() => {
        handleChange();
    }, [streetAddress, aptNo]);

    return (
        <div className="label-container" >
            <label>Address</label><input className="label-container-input" onChange={buildAddressForm} name="street_address" id="street_address"
                                            type="text"/>
            <label className="label-container-apt">Apt No</label><input className="default-margin-apt" onChange={buildAddressForm} name="apt_no" id="apt_no" type="text"/>
        </div>
    )
};

export default AddressFormComponent;
