import React, {Component} from 'react';
import {Redirect} from 'react-router-dom';
import Utils from '../../Utils/Util';
import {ajaxPost} from '../../Services/Http/Ajax';
import * as constants from '../../Utils/Constants'
import * as urls from '../../Utils/Urls'

import { Button, DropdownButton,FormGroup,Dropdown, FormControl, ControlLabel } from "react-bootstrap";


class HousingTypeFormComponent extends Component {
    constructor(props) {
        super(props);
        this.handleChange = this.handleChange.bind(this);
        this.state = {
            value: "",
            homeLessSelect: 0,
            housingType: []
        };
    }

    handleChange = (e) => {
        const {name, value} = e.target;
        this.setState({[name]: value});
        let childFamilyData = {
            housingData: {
                housing_type: e.target.value
            }
        };
        this.props.onSelectedChild(childFamilyData);
    };

    componentDidMount = () => {
        this.fetchHousingType()
    };

    fetchHousingType = () => {
        let housingData=[];
        let uri = urls.API_URL.GET_HOUSINGTYPE;
        let data = {
           housing_type: this.state.homeLessSelect
        };
        ajaxPost(uri, data, (response) => {
            if (response.status === constants.STATUS_ACTIVE) {
                housingData = response.content;
                let housingArray = housingData.map(types => {
                    return {types}
                });
                this.setState({
                    housingType: housingArray
                });
            }
        });
    };

    handleHomelessSelect = (e) => {
        this.setState({
            homeLessSelect: e.target.checked
        });
        this.fetchHousingType();
    };
    render() {
            return (
                <div>
                    <label>Homeless</label>
                    <input type="checkbox" checked = {this.state.homeLessSelect} name="housing_type_checkbox"  onChange={this.handleHomelessSelect} />
                    <select id="housing_type" name="housing_type" onChange={this.handleChange} >
                        <option value="">Select option</option>
                        {
                            this.state.housingType.map((value, index) => {
                                    return <option key={index} value={value.types.id}>{value.types.housing_type}</option>
                                }
                                )
                            }
                        }
                    </select>
                </div>
            )
        }
    }
export default HousingTypeFormComponent;
