/**
 * Created by basil on 20/2/20.
 */

import React from 'react';
import '../../App.css';
import {showMessage} from '../../Utils/Util';
import {STATUS_ACTIVE} from '../../Utils/Constants';
import {API_URL} from '../../Utils/Urls';
import {ajaxPost,ajaxPut} from '../../Services/Http/Ajax';
import Checkbox from "../General/CheckBoxComponent";
import {ToastContainer} from "react-toastify";

const ModulePermissionComponent = (props) => {
    const [moduleFetched, setModuleFetched] = React.useState(false);
    const [modulePermissionArray, setModulePermissionArray] = React.useState([]);
    const [isMainMenuActive, setIsMainMenuActive] = React.useState(false);
    const {moduleId, roleId,agencyId } = props;
    const usePrevious = (value) => {
        const ref = React.useRef();
        React.useEffect(() => {
            ref.current = value;
        });
        return ref.current;
    }; console.log('add');
    const previousModuleId = usePrevious(moduleId);
    const previousRoleId = usePrevious(roleId);
    const previousAgencyId = usePrevious(agencyId);
    const getModulePermissionData = () => {
        if (roleId && moduleId) {
            let postData = {
                data:{
                    roleId: roleId,
                    menuId: moduleId,
                    agencyId:agencyId
                }
            };
            let uri = API_URL.GET_MODULE_PERMISSION_URL;
          ajaxPost(uri, postData, getModulePermissionDataSuccess)
        }
    };
    const getModulePermissionDataSuccess = (response) => {
        console.log("ind");
        if (response.status === STATUS_ACTIVE) {
            let moduleData;
            moduleData = response.content;
            let menuNewTemp = moduleData.map((item)=> { return {...item, is_active: item.is_active === 1}  } );
            setSubMenuArray(menuNewTemp);
            setMenuFetched(true);
            setIsMainMenuActive(menuNewTemp[0].is_active);
        }
    };

    React.useEffect(() => {
        if(previousModuleId !== moduleId || previousRoleId.roleId !== roleId || previousAgencyId !== agencyId) {
            getModulePermissionData();
        }
    }, [moduleId, roleId,agencyId]);
    return (
        <div>
               Module Permisssionddd
        </div>
    )
};

export default ModulePermissionComponent;
