/**
 * Created by akshay on 20/1/20.
 */
import React, {Component} from 'react';
import '../../App.css';
import Button from 'react-bootstrap/Button';
import Jumbotron from 'react-bootstrap/Jumbotron';

class AreaComponent extends Component {
    constructor(props) {
        super(props);
        this.state = {
            value: "",
            currentDate: "",

        };
    }

    componentDidMount() {
        let today = new Date();
        let dd = String(today.getDate()).padStart(2, '0');
        let mm = String(today.getMonth() + 1).padStart(2, '0'); //January is 0!
        let yyyy = today.getFullYear();
        today = mm + '/' + dd + '/' + yyyy;
        return this.setState({
            currentDate: today
        });
    }

    render() {
        return (

            <div>
                <Jumbotron>
                <h4>Serve Families on {this.state.currentDate}</h4>
                <p>
                    <span>
                          <Button href="/family/create" variant="secondary">Food Pantry</Button>
                        <br/>
                    </span>
                    <span>
                        <br/>
                           <Button href="/family/create"  variant="secondary">Holiday Basket</Button>
                    </span>
                </p>
                <p>
                </p>
                </Jumbotron>
            </div>
        )
    }
}
export default AreaComponent ;
