/**
 * Created by Basil on 6/1/20.
 */
import React from 'react';
import MenuComponent from '../General/MenuComponent';
import FooterComponent from '../General/FooterComponent';
import RoleSelectionComponent from '../General/RoleSelectionComponent';
import MenuPermissionComponent from '../MenuPermission/MenuPermissionComponent';
import MenuListComponent from "../General/MenuListComponent";
import AgencySelectionComponent from "../General/AgencySelectionComponent";
import  MenuPermissionContext from "../../Store/ContextApi/MenuPermissionContext";
import '../../App.css';

const MenuPermissionContainer = () => {
    const [role, setRole] = React.useState('');
    const [mainMenu, setMainMenu] = React.useState('');
    const [agency, setAgency] = React.useState('');
    const [permission,setPermission] = React.useState({menuId:'',roleId:'',agencyId:''});

    const handleChangeInRole = (roleId) => {
        setRole(roleId);
        setPermission({roleId:roleId,menuId:mainMenu,agencyId:agency});
    };

    const handleChangeInMainMenu = (mainMenuId) => {
        setMainMenu(mainMenuId);
        setPermission({roleId:role,menuId:mainMenuId,agencyId:agency});
    };
    const handleChangeInAgency = (agencyId) => {
        setAgency(agencyId);
        setPermission({roleId:role,menuId:mainMenu,agencyId:agencyId});
    };
    return (
        <div>
            <MenuComponent/>
            <div className="main-container">
                <div className="data-container">
                    <RoleSelectionComponent onSelectedRole = {handleChangeInRole}/>
                    <MenuListComponent onSelectedMainMenu = {handleChangeInMainMenu}/>
                    <AgencySelectionComponent onSelectedAgency = {handleChangeInAgency}/>
                </div>
                <MenuPermissionContext.Provider value={permission} >
                    <MenuPermissionComponent />
                </MenuPermissionContext.Provider>

            </div>
            <FooterComponent/>
        </div>
    )

};

export default MenuPermissionContainer;
