import React, {Component} from 'react';
import {Redirect} from 'react-router-dom';
import useForm from '../../Utils/UseForm';

const ZipFormComponent = React.forwardRef((props, ref) =>  {
    const [zipCode, setZipCode] = React.useState('');
    let zip_code_form='';
    const [childFamilyData, setChildFamilyData] = React.useState([]);
    let data = '';

    let formDataBuildOne = props.famData ? ((zip_code_form=props.famData.zipcode))? props.famData : '' :'';

    React.useEffect(() => {
        if (zip_code_form) {
            setZipCode(zip_code_form);
        }
    }, [zip_code_form]);


    React.useEffect(() => {
        handleChange();
    }, [zipCode]);


    const buildForm = (e) => {
        setZipCode(e.target.value)
        };



    const handleChange = () => {
          data= { zipData :{
        zip_code: zipCode}};
        props.onSelectedChild(data);
    };



    const dataToParent = () => {
        props.onSelectedChild(childFamilyData);
    };

    const { errors, handleErrors } =
        useForm(props, {
            'zip_code' : ['required', 'integer','min:5', 'max:6'],
        }, dataToParent);

    React.useImperativeHandle(ref, () => ({

        triggerErrors(){
            handleChange();
            return handleErrors(data.zipData);
        }

    }));


    return (
        <div className="label-container">
           <label>Zip code</label><input className="default-margin-zip" defaultValue={zip_code_form} onChange={buildForm}  onBlur={handleErrors}   name="zip_code" id="zip_code" type="text"/>


            {errors.zip_code && (
                <span className="validationError">{errors.zip_code}</span>
            )}
        </div>
    )
});

export default ZipFormComponent;
