/**
 * Created by abhiram on 7/1/20.
 */

import React, {Component} from 'react';
import Utils from '../../Utils/Util';
import {Link} from "react-router-dom";
import LogoutComponent from "../Login/LogoutComponent";
import {Nav,NavDropdown,Image,Col} from 'react-bootstrap';

class MenuComponent extends Component {
    constructor(props) {
        super(props);
        this.permissions = JSON.parse(Utils.getItemLocalStorage('menu_permission'));
        this.permissionArray = Object.keys(this.permissions).map(i => this.permissions[i]);
    }

    render() {
        return (
            <div className="header">
                <div className="header-menu">
                    <div className="fresh-trak-logo"><Link to="/dashboard">FreshTrak</Link></div>
                    <Nav>


                        <Link to="/dashboard">Home</Link>
                             { this.permissionArray.map((value, index) => {
                            return (
                                <NavDropdown title={value[0].menu_name} key={index} id="nav-dropdown">
                                    {
                                        value.map((valueInner, indexInner) => {
                                          return(<NavDropdown.Item className="sub-menu" key={indexInner}><Link
                                              to={valueInner.menu_link}>{valueInner.sub_menu_name}</Link></NavDropdown.Item>
                                          )
                                        })

                                    }
                                </NavDropdown>
                            )
                        })}

                    </Nav><LogoutComponent/>
                </div>
            </div>
        )
    }
}
export default MenuComponent;
