/**
 * Created by kripa on 22/1/20.
 */

import React,{Component} from 'react';
import {ajaxGet} from '../../Services/Http/Ajax';
import * as constants from '../../Utils/Constants'
import '../../App.css';


class MenuListComponent extends Component{
    constructor(props){
        super(props);
        this.state = {
            menuValue: [],
        };
    };

        componentDidMount = () => {
        let menuData;
        let uri = constants.BASE_URL + '/api/menu';
        ajaxGet(uri, (response) => {
            if (response.status === constants.STATUS_ACTIVE) {
                menuData = response.content;
                let menuArray = menuData.map(menu => {
                    return {menu}
                });
                this.setState({
                    menuValue: menuArray,
                });
            }
        });
    };

        render() {
        return (
            <div>
                <span>Menu: </span>
                <select name="" id="">
                    <option value="">Select Options</option>
                    {this.state.menuValue.map( (value, index) => {
                        return <option value={value.menu.id} key={index}>{value.menu.menu_name}</option>
                    })  }
                </select>
            </div>
        )
    }
}


export default MenuListComponent;
