/**
 * Created by abhiram on 7/1/20.
 */

import React, {Component} from 'react';
import Utils from '../../Utils/Util';
import {Link} from "react-router-dom";
import LogoutComponent from "../Login/LogoutComponent";
import Navbar from 'react-bootstrap/Navbar';
import {Nav,NavDropdown} from 'react-bootstrap';
import Form from 'react-bootstrap/Form';
import FormControl from 'react-bootstrap/FormControl';
import Button from 'react-bootstrap/Button'

class MenuComponent extends Component {
    constructor(props) {
        super(props);
        this.permissions = JSON.parse(Utils.getItemLocalStorage('menu_permission'));
        this.permissionArray = Object.keys(this.permissions).map(i => this.permissions[i]);
    }

    render() {
        return (
            <div className="header">
                <Link to="/dashboard" className="logo">FreshTrak</Link>
                <LogoutComponent/>
                <div className="list-group">
                   {/* <ul>
                        { this.permissionArray.map((value, index) => {
                            return (
                                <div className="list-item menuList" key={index}>
                                    <ul><u>{value[0].menu_name}</u>
                                        {
                                            value.map((valueInner, indexInner) => {
                                                return (
                                                    <li className="sub-menu" key={indexInner}><Link
                                                        to={valueInner.menu_link}>{valueInner.sub_menu_name}</Link></li>

                                                )
                                            })
                                        }
                                    </ul>
                                </div>
                            )
                        })}
                    </ul>*/}
                    <Nav variant="pills" activeKey="1">

                        { this.permissionArray.map((value, index) => {
                            return ( <NavDropdown title={value[0].menu_name} id="nav-dropdown">
                                    <NavDropdown.Item eventKey="4.1">Action</NavDropdown.Item>
                                    <NavDropdown.Item eventKey="4.2">Another action</NavDropdown.Item>
                                    <NavDropdown.Item eventKey="4.3">Something else here</NavDropdown.Item>
                                    <NavDropdown.Divider />
                                    <NavDropdown.Item eventKey="4.4">Separated link</NavDropdown.Item>
                                </NavDropdown>
                            )
                        })}
                    </Nav>
                </div>
            </div>
        )
    }
}
export default MenuComponent;
