import React from 'react';

const DobFormComponent = (props) => {

    const [dob, setDob] = React.useState('');
    const [age, setAge] = React.useState('');
    const [gender, setGender] = React.useState('');
    let age_form,dob_form,gender_digit='';
    let gender_form=false;

    const buildDobForm = (e) => {
        let { name, value } = e.target;
        switch (name) {
            case 'gender':
                setGender(value);
                break;
            case 'dob':
                setDob(value);
                break;
            case 'age':
                setAge(value);
                break;
            default:
                break;
        }

    };

    React.useEffect(() => {
        handleChange();
    }, [dob, age, gender]);

    const handleChange = () => {
        let childFamilyData = {
            dobData: {
                dob: dob,
                age: age,
                gender: gender
            }
        };
        props.onSelectedChild(childFamilyData);
    };

    const getAge=()=>{
        let dobString=dob;
        let today = new Date();
        let birthDate = new Date(dobString);
        let age = today.getFullYear() - birthDate.getFullYear();
        let m = today.getMonth() - birthDate.getMonth();
        if (m < 0 || (m === 0 && today.getDate() < birthDate.getDate())) {
            age--;
        }
        return setAge(age);
    };

    let formDataBuildOne = props.famData ? ((gender_digit=props.famData.gender))? props.famData : '' :'';
    let formDataBuildTwo = props.famData ? ((dob_form=props.famData.dob ))? props.famData : '' :'';
    let formDataBuildThree = props.famData ? ((age_form=props.famData.age ))? props.famData : '' :'';


    React.useEffect(() => {
        if (gender_digit) {
            setDob(dob_form);
            setAge(age_form);
            setGender(gender_digit);

        }
    }, [gender_digit]);


    return (
        <div className="label-container">

                <label>DOB</label>

                <input  className="default-margin-dob"  onChange={buildDobForm} defaultValue={dob_form}  onBlur={getAge} name="dob" id="dob" type="date" min='1899-01-01' />


            <label className="label-container-age">Age</label>
                <input defaultValue={age_form?age_form:age} onChange={buildDobForm}  name="age" id="age" type="number" />
            <div>
                <div>
                    <label>Please select your gender:</label>
                    <input type="radio" name="gender"  value="1" defaultChecked={gender_digit == 1 ? true : ''}  onChange={buildDobForm}/><span> Male </span>
                    <input type="radio" name="gender" value="2"  defaultChecked={gender_digit == 1 ? true : ''}  onChange={buildDobForm} /> <span> Female </span>
                    <input type="radio" name="gender" value="3"  defaultChecked={gender_digit == 1 ? true : ''} onChange={buildDobForm} /><span>Other</span>
                </div>
           </div>
        </div>
    )
};

export default DobFormComponent;
