/**
 * Created by akshay on 22/1/20.
 */
import React, {Component} from 'react';
import {ajaxGet} from '../../Services/Http/Ajax';
import * as constants from '../../Utils/Constants'
import '../../App.css';

class RoleSelectionComponent extends Component {

    constructor(props) {
        super(props);
        this.state = {
            roleValue: [],
        };
    };

    componentDidMount = () => {
        let roleData;
        let uri = constants.BASE_URL + '/api/roles';
        ajaxGet(uri, (response) => {
            if (response.status === constants.STATUS_ACTIVE) {
                roleData = response.content;
                let roleArray = roleData.map(roles => {
                    return {roles}
                });
                this.setState({
                    roleValue: roleArray,
                });
            }
        });
    };

    render() {
        return (
            <div className="role-data">
                <span>Role: </span>
                <select name="" id="">
                    <option value="">Select Options</option>
                    {this.state.roleValue.map( (value, index) => {
                        return <option value={value.roles.id} key={index}>{value.roles.role_name}</option>
                    })  }
                </select>
            </div>
        )
    }
}
export default RoleSelectionComponent ;
