import React, {Component} from 'react';
import {Redirect} from 'react-router-dom';
import {ajaxGet} from '../../Services/Http/Ajax';
import {STATUS_ACTIVE} from '../../Utils/Constants';
import {API_URL} from '../../Utils/Urls';

const NameFormComponent = (props) => {
    const [firstName, setFirstName] = React.useState('');
    const [lastName, setLastName] = React.useState('');
    const [middleName, setMiddleName] = React.useState('');
    const [suffix, setSuffix] = React.useState('');
    const [suffixArray, setSuffixArray] = React.useState([]);
    let first_name,last_name,middle_name,suffix_form_data='';
    let test='';

    React.useEffect(() => {
        let uri = API_URL.GET_SUFFIX;
        ajaxGet(uri, (response) => {
            if (response.status === STATUS_ACTIVE) {
                setSuffixArray(response.content);
            }

        });
    }, []);


    const buildNameForm = (e) => {
        let { name, value } = e.target;
        let setFunction = '';
      switch (name) {
          case 'first_name':
              setFunction = setFirstName;
              break;
          case 'last_name':
              setFunction = setLastName;
              break;
          case 'middle_name':
              setFunction = setMiddleName;
              break;
          case 'Suffix':
              setFunction = setSuffix;
              break;
          default:
              break;
      }

      if (setFunction !== '') {
          setFunction(value)
      }
    };

    React.useEffect(() => {
        handleChange();
    }, [firstName, lastName, middleName, suffix]);



    const handleChange = () => {
        let  childFamilyData= { nameData :{
            first_name: firstName,
            last_name: lastName,
            middle_name: middleName,
            suffix : suffix
        }
        };
         props.onSelectedChild(childFamilyData);
    };


    let formDataBuildOne = props.famData ? ((first_name= props.famData.first_name) )? props.famData : '' :'';
    let formDataBuildtwo = props.famData ? ((middle_name=props.famData.middle_name))? props.famData : '' :'';
    let formDataBuildthree = props.famData ? ((last_name=props.famData.last_name))? props.famData : '' :'';
    let formDataBuildfour = props.famData ? ((suffix_form_data=props.famData.suffix))? props.famData : '' :'';


    return (
        <div id="fullname">
            <div className="label-container">

            <label>First  name</label><input className="default-margin-name"  onChange={buildNameForm} name="first_name" id="first_name"
                                                 type="text" defaultValue={first_name} />
            <label>Last name</label><input  className="label-container-lst" onChange={buildNameForm} name="last_name" id="last_name"
                                              type="text" defaultValue={last_name}  />
            <label>Middle name</label><input onChange={buildNameForm} defaultValue={middle_name}  name="middle_name" id="middle_name"
                                                type="text"/>
            <div/>
                <div className="label-container">
                    <label>Suffix</label>

                    <select  id="suffix" name="Suffix"  className="default-margin"  onChange={buildNameForm}>
                {suffix_form_data && (
                    <option selected="selected">
                        {suffix_form_data}
                    </option>)}
                <option  >Select option</option>
                {suffixArray.map((value, index) => {
                    return <option value={value.suffix_type}    key={index}>{value.suffix_type}</option>
                })  }
            </select>
                </div>
            </div>
        </div>
    )
};

export default NameFormComponent;
