/**
 * Created by Basil on 03/02/2020.
 */
import React, {Component} from 'react';
import {ajaxGet} from '../../Services/Http/Ajax';
import {STATUS_ACTIVE}from '../../Utils/Constants'
import {API_URL} from '../../Utils/Urls'
import '../../App.css';

const AgencySelectionComponent = (props) =>{
    const [agencyValue, setAgencyValue] = React.useState([]);
    const [selectedAgency,setSelectedAgency] = React.useState('');
    const  handleChangeInAgency = (event) => {
        setSelectedAgency(event.target.value);
        props.onSelectedAgency(event.target.value);
    };
    React.useEffect(() => {
        let uri = API_URL.GET_AGENCY_LIST_URL1;
        ajaxGet(uri, (response) => {
            if (response.status === STATUS_ACTIVE) {
                setAgencyValue(response.content);
            }
        });
    }, []);
    return (
        <div className="agency-data">
            <span className="btn">Agency: </span>
            <select className="dropdown-toggle btn btn-secondary" onChange={handleChangeInAgency}>
                <option className="dropdown-item" value="">Select Options</option>
                {agencyValue.map( (value, index) => {
                    return <option className="dropdown-item" value={value.id} key={index}>{value.name}</option>
                })  }
            </select>
        </div>
    )
}
export default AgencySelectionComponent;
