/**
 * Created by kripa on 22/1/20.
 */

import React, {Component} from 'react';
import {ajaxGet} from '../../Services/Http/Ajax';
import * as constants from '../../Utils/Constants'
import * as urls from '../../Utils/Urls'
import '../../App.css';


class MenuListComponent extends Component {
    constructor(props) {
        super(props);
        this.state = {
            menuValue: [],
            selectedMainMenu: null
        };
    };

    componentDidMount = () => {
        let menuData;
        let uri = urls.API_URL.GET_MENU_LIST_URL;
        ajaxGet(uri, (response) => {
            if (response.status === constants.STATUS_ACTIVE) {
                menuData = response.content;
                let menuArray = menuData.map(menu => {
                    return {menu}
                });
                this.setState({
                    menuValue: menuArray,
                });
            }
        });
    };

    handleChangeInMainMenu = (e) => {
        this.setState({
            selectedMainMenu: e.target.value
        });
        this.props.onSelectedMainMenu(e.target.value);
    };

    render() {
        return (
            <div className="menu-data">
                <span className="btn">Menu: </span>
                <select className="dropdown-toggle btn btn-secondary" onChange={this.handleChangeInMainMenu}>
                    <option className="dropdown-item" value="">Select Options</option>
                    {this.state.menuValue.map((value, index) => {
                        return <option className="dropdown-item" value={value.menu.id} key={index}>{value.menu.menu_name}</option>
                    })  }
                </select>
            </div>
        )
    }
}


export default MenuListComponent;
