 */
import React, {Component} from 'react';
import '../../App.css';
import * as constants from '../../Utils/Constants';
import {ajaxPost} from '../../Services/Http/Ajax';
import Checkbox from "../General/CheckBoxComponent";
class MenuPermissionComponent extends Component {

    constructor(props) {
        super(props);
        this.state = {
            checkedItems: new Map(),
            menuFetched: false,
            subMenuArray: [],
            isMainMenuActive: false
        }
        this.handleChange = this.handleChange.bind(this);
    }

    componentDidUpdate = (prevProps) => {
       this.getMenuPermissionData(prevProps);
    };
    getMenuPermissionData = (prevProps)=>{
        if((prevProps.menuId !== this.props.menuId) || prevProps.roleId !== this.props.roleId) {
            let menuData;
            let roleId = this.props.roleId;
            let mainMenuId = this.props.menuId;
            if (roleId && mainMenuId) {
                let data = {
                    roleId: roleId,
                    menuId: mainMenuId
                };
                let uri = constants.BASE_URL + '/api/menu/permission';
                ajaxPost(uri, data, (response) => {
                    if (response.status === constants.STATUS_ACTIVE) {
                        menuData = response.content;
                        let subMenuArray = menuData.map(subMenus => {
                            const item = subMenus.subMenuId;
                            const isChecked = subMenus.is_active === 1;
                            this.setState(prevState => ({ checkedItems: prevState.checkedItems.set(item, isChecked) }))
                            return {subMenus}
                        });
                        this.setState({
                            subMenuArray: subMenuArray,
                            menuFetched: true,
                            isMainMenuActive: subMenuArray[0].subMenus.is_active
                        });
                    }
                })
            }
        }
    }
    putMenuPermmissionData = (e)=>{
        let putData = {

        }
        console.log("basil",e.target.id,'>>>',e.target.name);
    }

    makeSubMenuCheckBoxDisabled = (e) => {
        this.setState({
            isMainMenuActive: e.target.checked
        });
    };
    handleChange(e) {
        const item = parseInt(e.target.id);
        const isChecked = e.target.checked;
        this.setState(prevState => ({ checkedItems: prevState.checkedItems.set(item, isChecked) }));
        this.putMenuPermmissionData(e);
    }
    render() {
        const { menuFetched } = this.state;
        const menuDetails = this.state.subMenuArray;
        return (
            <div>MenuComponent
                <div className="menu-permission">
                    {
                        menuFetched && (
                        <div> <b> {menuDetails[0].subMenus.menu_name} <input onChange={this.makeSubMenuCheckBoxDisabled} checked={this.state.isMainMenuActive} type="checkBox" name="menuPermission"/></b>
                            { menuDetails.map( (value, index) => {
                                return <div key={index}>{value.subMenus.sub_menu_name} <Checkbox name={`submenu-${value.subMenus.subMenuId}`} id={value.subMenus.subMenuId} checked={this.state.checkedItems.get((value.subMenus.subMenuId))} disabled = {!this.state.isMainMenuActive} onChange={this.handleChange} /></div>
                            })  }
                        </div>
                    )}
                </div>
            </div>
        )
    }
}
export default MenuPermissionComponent;
