/**
 * Created by Basil on 6/1/20.
 */
import React from 'react';
import '../../App.css';
import {showMessage} from '../../Utils/Util';
import {STATUS_ACTIVE} from '../../Utils/Constants';
import {API_URL} from '../../Utils/Urls';
import {ajaxPost,ajaxPut} from '../../Services/Http/Ajax';
import Checkbox from "../General/CheckBoxComponent";
import {ToastContainer} from "react-toastify";

const MenuPermissionComponent = (props) => {
    const [menuFetched, setMenuFetched] = React.useState(false);
    const [subMenuArray, setSubMenuArray] = React.useState([]);
    const [isMainMenuActive, setIsMainMenuActive] = React.useState(false);
    const {menuId, roleId,agencyId } = props;

    const usePrevious = (value) => {
        const ref = React.useRef();
        React.useEffect(() => {
            ref.current = value;
        });
        return ref.current;
    };
    const previousMenuId = usePrevious(menuId);
    const previousRoleId = usePrevious(roleId);
    const previousAgencyId = usePrevious(agencyId);
    const getMenuPermissionData = () => {
        if (roleId && menuId) {
            let postData = {
                data:{
                    roleId: roleId,
                    menuId: menuId,
                    agencyId:agencyId
                }
            };
            let uri = API_URL.MENU_PERMISSION_URL;
            ajaxPost(uri, postData, getMenuPermissionDataSuccess)
        }
    };

    const getMenuPermissionDataSuccess = (response) => {
        if (response.status === STATUS_ACTIVE) {
            let menuData;
            menuData = response.content;
            let menuNewTemp = menuData.map((item)=> { return {...item, is_active: item.is_active === 1}  } );
            setSubMenuArray(menuNewTemp);
            setMenuFetched(true);
            setIsMainMenuActive(menuNewTemp[0].is_active);
        }
    };

    React.useEffect(() => {
        console.log(previousMenuId ,'previousRoleId.roleId >>>>>>>>>>>>>');
        if(previousMenuId !== menuId || previousRoleId.roleId !== roleId || previousAgencyId !== agencyId) {
            getMenuPermissionData();
        }
    }, [menuId, roleId,agencyId]);

    const putMenuPermissionData = (event) => {
        let data = {
            data: {
                menu_permission_id: event.target.getAttribute("dataid"),
                is_active: event.target.checked,
                role_id: props.roleId,
                sub_menu_id: event.target.id,
                agency_id:props.agencyId
            }
        };
        let uri = API_URL.MENU_PERMISSION_URL;
        ajaxPut(uri, data, (response) => {
            if (response.status === STATUS_ACTIVE) {
                showMessage('success', response.message)
            }
        });
    };

    const makeSubMenuCheckBoxDisabled = (e) => {
        setIsMainMenuActive(e.target.checked);
    };

    const handleChange = (e) => {
        const item = parseInt(e.target.id);
        setSubMenuArray(subMenuArray.map((elem)=>{
            let tempData = elem;
            if(tempData.sub_menu_id === item){
                tempData.is_active = e.target.checked;
            }
            return tempData;
        }));
        putMenuPermissionData(e);
    };

    return (
        <div>
            <ToastContainer/>
            <div className="menu-permission">
                {
                    menuFetched && (
                        <div className="data-bind-role">
                            <span className="btn text-info"> {subMenuArray[0].menu_name} <input onChange={makeSubMenuCheckBoxDisabled} checked={isMainMenuActive} type="checkBox" name="menuPermission"/></span>
                            <div>
                                { subMenuArray.map( (value, index) => {
                                    return <div key={index}>
                                        <div className="sub-menu-container">
                                            <div>{value.sub_menu_name} </div>
                                            <div> <Checkbox name={`submenu-${value.sub_menu_id}`} dataid={value.menu_permission_id} id={value.sub_menu_id} checked={value['is_active']} disabled = {!isMainMenuActive} onChangeFunction={handleChange} /></div>
                                        </div>
                                    </div>
                                })
                                }
                            </div>
                        </div>
                    )}
            </div>
        </div>
    )
};

export default MenuPermissionComponent;
